Item.create(title:'Todos lo saben' , year:2018 ,  director:'Asghar Farhadi' ,  description:'Todos lo saben es una película de 2018 dirigida y escrita por Asghar Farhadi, es un coproducción entre España, Francia e Italia y está protagonizada por Penélope Cruz, Javier Bardem y Ricardo Darín. Su rodaje comenzó el 21 de agosto de 2017 en España​ y finalizaba en diciembre del mismo año.' ,  duration:132 , link:'www.netflix.com' , type_item:0)
